import { useState } from 'react';
import {
  Button,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
} from "@chakra-ui/react";

function ChangeProfilePhotoModal({ isOpen, onOpen, onClose, handleSaveProfilePhoto }) {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleProfileImageChange = (event) => {
    const file = event.target.files[0];
    setSelectedImage(file);
  };

  const handleSaveClick = () => {
    if (selectedImage) {
      handleSaveProfilePhoto(selectedImage);
    }
    onClose();
  };

  return (
    <>
      <Modal onClose={onClose} isOpen={isOpen} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader textAlign="center">Change Profile Photo</ModalHeader>

          <ModalBody>
            <div className="flex flex-col items-center">
              <label
                htmlFor="profileImage"
                className="font-bold py-3 text-blue-600 text-center cursor-pointer text-xs w-full"
              >
                Upload Photo
              </label>

              <input
                onChange={handleProfileImageChange}
                type="file"
                id="profileImage"
                name="profileImage"
              />
            </div>

            <hr />

            <p className="font-bold py-3 text-red-600 text-center">
              Remove Photo
            </p>
            <hr />
            <p className="py-3 text-center" onClick={onClose}>
              Cancel
            </p>
          </ModalBody>
          <ModalFooter>
            <Button onClick={handleSaveClick}>Save</Button>
            <Button onClick={onClose} ml={4}>Close</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
}

export default ChangeProfilePhotoModal;
